INSERT INTO `wp_3posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-10-08 22:04:34', '2013-10-08 22:04:34', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2013-10-08 22:04:34', '2013-10-08 22:04:34', '', 0, 'http://localhost:8888/bootstrap_2/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2013-10-08 22:04:34', '2013-10-08 22:04:34', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://localhost:8888/bootstrap_2/wordpress/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2013-10-08 22:04:34', '2013-10-08 22:04:34', '', 0, 'http://localhost:8888/bootstrap_2/wordpress/?page_id=2', 0, 'page', '', 0),
(3, 1, '2013-10-08 22:04:47', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2013-10-08 22:04:47', '0000-00-00 00:00:00', '', 0, 'http://localhost:8888/bootstrap_2/wordpress/?p=3', 0, 'post', '', 0);
